public class Part3 {
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int currIndex = dna.indexOf(stopCodon, startIndex + 3);
        while (currIndex != -1){
        int diff = currIndex - startIndex;
        if (diff % 3 == 0){
            return currIndex;
        }
        else {
            currIndex = dna.indexOf(stopCodon, currIndex+1);
        }
        }
        return -1;
    }
    public String findGene (String dna, int where){
        
        int startIndex = dna.indexOf("ATG", where);
        if (startIndex == -1){
            return "";
        }
        int taaIndex = findStopCodon (dna, startIndex, "TAA");
        int tagIndex = findStopCodon (dna, startIndex, "TAG");
        int tgaIndex = findStopCodon (dna, startIndex, "TGA");
        //int tmp = Math.min (taaIndex, tagIndex);
        int minIndex = 0;
        //int minIndex = Math.min (tmp, tgaIndex);
        if (taaIndex == -1 || (tgaIndex != -1 && tgaIndex < taaIndex)){
            minIndex = tgaIndex;
        }
        else {
            minIndex = taaIndex;
        }
        if (minIndex == -1 || (tagIndex != -1 && tagIndex < minIndex)){
            minIndex = tagIndex;
        }
        if (minIndex == -1){
            return "";
        }
        return dna.substring (startIndex, minIndex + 3);
    }
    public int printAllGenes(String dna){
        int startIndex = 0;
        System.out.println ("The dna strand under review is " + dna);
        int counter = 0;
        while (true){
            String currGene = findGene(dna, startIndex);
            if (currGene.isEmpty()){
                break;
            }
            System.out.println(currGene);
            counter ++;
            startIndex = dna.indexOf(currGene, startIndex) + currGene.length();
        }
        return counter;
    }
    public int countGenes(String dna){
        int result = printAllGenes(dna);
        return result;
    }
    public void testCountGenes(){
        String dna = "AAATGCCCTAACTATGTTAAGATGAAACC";
        
        int count = countGenes(dna);
        System.out.println (count);
        
        dna = "";

        count = countGenes(dna);
        System.out.println (count);
        
        dna = "AAATGCCCTAACTATGTTAAGATGAAACCATGCCCCCCCCCTAG";
        count = countGenes(dna);
        System.out.println (count);
        
        
    }
}
